CREATE OR REPLACE FUNCTION san_obter_situacao_hemocomp ( ie_opcao_p text, nr_seq_transfusao_p bigint) RETURNS varchar AS $body$
DECLARE

ie_status_w 	varchar(1);


BEGIN
if (ie_opcao_p = 'I') then
	select  max(ie_irradiado)
	into STRICT	ie_status_w
        from	san_producao
	where	nr_seq_transfusao = nr_seq_transfusao_p;


elsif (ie_opcao_p = 'F') then
	select  max(ie_filtrado)
	into STRICT	ie_status_w
        from	san_producao
	where	nr_seq_transfusao = nr_seq_transfusao_p;

elsif (ie_opcao_p = 'L') then
	select  max(ie_lavado)
	into STRICT	ie_status_w
        from	san_producao
	where	nr_seq_transfusao = nr_seq_transfusao_p;

elsif (ie_opcao_p = 'A') then
	select  max(ie_aliquotado)
	into STRICT	ie_status_w
        from	san_producao
	where	nr_seq_transfusao = nr_seq_transfusao_p;
end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
