CREATE OR REPLACE FUNCTION san_obter_status_adep ( nr_seq_reserva_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_reserva_prod_w	bigint;
nr_seq_producao_w		bigint;
ds_hemo_suspenso_adep_w	varchar(255) := null;
ds_hemocomponente_w		varchar(255) := null;


C01 CURSOR FOR
	SELECT	nr_sequencia,
			nr_seq_producao
	from   	san_reserva_prod
	where 	nr_seq_reserva	= nr_seq_reserva_p
	and    	ie_status_adep	= 'S';


BEGIN

if (nr_seq_reserva_p IS NOT NULL AND nr_seq_reserva_p::text <> '') then

open C01;
loop
fetch C01 into
	nr_seq_reserva_prod_w,
	nr_seq_producao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		select	substr(obter_desc_san_derivado(nr_seq_derivado),1,50)
		into STRICT	ds_hemocomponente_w
		from	san_producao
		where 	nr_sequencia = nr_seq_producao_w;

		if (coalesce(ds_hemo_suspenso_adep_w::text, '') = '') and (ds_hemocomponente_w IS NOT NULL AND ds_hemocomponente_w::text <> '') then

			ds_hemo_suspenso_adep_w := ds_hemocomponente_w;

		elsif (ds_hemo_suspenso_adep_w IS NOT NULL AND ds_hemo_suspenso_adep_w::text <> '') and (ds_hemocomponente_w IS NOT NULL AND ds_hemocomponente_w::text <> '') then
			ds_hemo_suspenso_adep_w := substr(ds_hemo_suspenso_adep_w || ',' || ds_hemocomponente_w,1,255);
		end if;

	end;
end loop;
close C01;

end if;

return	ds_hemo_suspenso_adep_w;

end;
$body$
