CREATE OR REPLACE FUNCTION san_obter_tempo_cq (nr_seq_producao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_tempo_dias_cq_w	bigint;


BEGIN

if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then

	select	trunc(max(a.dt_liberacao)-max(a.dt_realizacao))
	into STRICT	qt_tempo_dias_cq_w
	from	san_controle_qualidade a,
		san_controle_qual_prod b
	where	a.nr_sequencia = b.nr_seq_qualidade
	and	b.nr_seq_producao = nr_seq_producao_p;

end if;


return	qt_tempo_dias_cq_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
