CREATE OR REPLACE FUNCTION san_obter_tipo_doacao_rel6 (nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(50);

BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	select	substr(b.ds_tipo_doacao,1,50)
	into STRICT	ds_retorno_w
	from	san_doacao a,
		san_tipo_doacao b
	where	b.nr_sequencia = a.nr_seq_tipo
	and	ie_classif_doacao = 'A'
	and	a.nr_sequencia = nr_seq_doacao_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
