CREATE OR REPLACE FUNCTION san_obter_tipo_doador_atual ( cd_pessoa_fisica_p text, dt_doacao_p timestamp) RETURNS varchar AS $body$
DECLARE


dt_final_w		timestamp := dt_doacao_p;
dt_inicial_w		timestamp := PKG_DATE_UTILS.ADD_MONTH(dt_doacao_p,-12,0);
qt_doacoes_w		bigint;
ie_tipo_doador_w	varchar(2);


BEGIN

select	count(*)
into STRICT	qt_doacoes_w
from	san_doacao a
where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
and	a.ie_avaliacao_final = 'A';

if (qt_doacoes_w = 0) then
	ie_tipo_doador_w := 'P';
else
	select	count(*)
	into STRICT	qt_doacoes_w
	from	san_doacao a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.ie_avaliacao_final = 'A'
	and	a.dt_doacao between PKG_DATE_UTILS.start_of(dt_inicial_w,'dd',0) and fim_dia(PKG_DATE_UTILS.start_of(dt_final_w,'dd',0));

	if (qt_doacoes_w = 0) then
		ie_tipo_doador_w := 'E';
	else
		ie_tipo_doador_w := 'S';
	end if;
end if;

return	ie_tipo_doador_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
