SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_obter_validade_solict ( ie_tipo_p text, nr_seq_reserva_p text) RETURNS varchar AS $body$
DECLARE



-- VS - Validade da Solicitação
validade_solicitacao_w	bigint;
valid_reserva_w		varchar(2);
ds_retorno_w		varchar(2);


BEGIN
ds_retorno_w := 'N';
if (ie_tipo_p IS NOT NULL AND ie_tipo_p::text <> '' AND nr_seq_reserva_p IS NOT NULL AND nr_seq_reserva_p::text <> '') then
	select  max(qt_validade_solic_reserva)
	into STRICT	validade_solicitacao_w
	from 	san_parametro
	where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

	if ( ie_tipo_p = 'VS') then
		select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	valid_reserva_w
		from	san_reserva a
		where	a.nr_sequencia = nr_seq_reserva_p
		and  	coalesce(ie_status,'N') = 'S'
		and (a.dt_reserva + validade_solicitacao_w/24) <= clock_timestamp();
	end if;

	if ( ie_tipo_p = 'VS') then
		ds_retorno_w := valid_reserva_w;
	end if;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION san_obter_validade_solict ( ie_tipo_p text, nr_seq_reserva_p text) FROM PUBLIC;

