CREATE OR REPLACE FUNCTION san_obter_volume_original ( nr_seq_producao_p bigint) RETURNS varchar AS $body$
DECLARE



qt_volume_w  		bigint;
qt_volume_atual_w 	bigint;


BEGIN

if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then

	select  sum(qt_volume)
	into STRICT  	qt_volume_w
	from   	san_producao
	where   nr_seq_prod_origem = nr_seq_producao_p;

	select  sum(qt_volume)
	into STRICT  	qt_volume_atual_w
	from   	san_producao
	where   nr_sequencia = nr_seq_producao_p;

qt_volume_atual_w := qt_volume_atual_w + qt_volume_w;

end if;

return	qt_volume_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
