CREATE OR REPLACE FUNCTION san_obter_volume_retirar (nr_prescricao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_volume_retirar_w	 cpoe_hemoterapia.qt_volume_retirar%type;
nr_seq_cpoe_w		prescr_solic_bco_sangue.nr_seq_hemo_cpoe%type;


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) then

	select max(nr_seq_hemo_cpoe)
	into STRICT	nr_seq_cpoe_w
	from prescr_solic_bco_sangue
	where nr_prescricao = nr_prescricao_p;

	if (coalesce(nr_seq_cpoe_w,0) > 0) then

		select	max(qt_volume_retirar)
		into STRICT	qt_volume_retirar_w
		from	cpoe_hemoterapia
		where	nr_sequencia = nr_seq_cpoe_w;

	end if;

end if;

return	coalesce(qt_volume_retirar_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
