CREATE OR REPLACE FUNCTION san_obt_dt_vencimento_hemocomp (nr_seq_producao_p bigint, nr_seq_derivado_p bigint, dt_producao_p timestamp) RETURNS timestamp AS $body$
DECLARE


hr_final_venciment_hemo_w	varchar(10);
nr_seq_derivado_w		bigint;
dt_producao_w			timestamp;
dt_vencimento_w			timestamp;

BEGIN
hr_final_venciment_hemo_w := obter_valor_param_usuario(450,262,obter_perfil_ativo,wheb_usuario_pck.get_nm_usuario,wheb_usuario_pck.get_cd_estabelecimento);

if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then
	select	nr_seq_derivado,
		dt_producao
	into STRICT	nr_seq_derivado_w,
		dt_producao_w
	from	san_producao
	where	nr_sequencia = nr_seq_producao_p;

	select	to_date(to_char(dt_producao_w + qt_dias_validade,'dd/mm/yyyy')||' '||coalesce(hr_final_venciment_hemo_w,'00:00:00'),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	dt_vencimento_w
	from	san_derivado
	where	nr_sequencia	= nr_seq_derivado_w;

elsif (nr_seq_derivado_p IS NOT NULL AND nr_seq_derivado_p::text <> '') then
	select	to_date(to_char(coalesce(dt_producao_p,clock_timestamp()) + qt_dias_validade,'dd/mm/yyyy')||' '||coalesce(hr_final_venciment_hemo_w,'00:00:00'),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	dt_vencimento_w
	from	san_derivado
	where	nr_sequencia	= nr_seq_derivado_p;
end if;

return	dt_vencimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
