CREATE OR REPLACE FUNCTION san_onter_atend_producao (nr_seq_producao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_transfusao_w	bigint;
nr_atendimento_w	bigint;


BEGIN

select 	max(nr_seq_transfusao)
into STRICT	nr_seq_transfusao_w
from	san_producao
where	nr_sequencia = nr_seq_producao_p;

if (nr_seq_transfusao_w IS NOT NULL AND nr_seq_transfusao_w::text <> '') then

	select 	max(nr_atendimento)
	into STRICT	nr_atendimento_w
	from 	san_transfusao
