CREATE OR REPLACE FUNCTION san_possui_prod_utilizada ( nr_seq_reserva_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
qt_producao_w		bigint;
nr_seq_producao_w	bigint;
ie_retorno_w		varchar(1);
			
C01 CURSOR FOR 
	SELECT 	nr_seq_producao 
	from 	san_exame_lote 
	where 	nr_seq_reserva = nr_seq_reserva_p 
	and 	(nr_seq_producao IS NOT NULL AND nr_seq_producao::text <> '') 
	and 	ie_origem = 'R';
	

BEGIN 
ie_retorno_w := 'N';
qt_producao_w := 0;
if (ie_opcao_p = 'R') then 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_producao_w;
	EXIT WHEN NOT FOUND or qt_producao_w > 0;  /* apply on C01 */
		begin 
		select	count(*) 
		into STRICT	qt_producao_w 
		from	san_producao_v 
		where	nr_sequencia = nr_seq_producao_w;
		end;
	end loop;
	close C01;
 
 
	if (qt_producao_w > 0) then 
		ie_retorno_w := 'S';
	end if;
elsif (ie_opcao_p = 'T') then 
	 
	select	count(*) 
	into STRICT	qt_producao_w 
	from	san_producao_v 
	where	nr_seq_transfusao = nr_seq_reserva_p;	
 
	if (qt_producao_w > 0) then 
		ie_retorno_w := 'S';
	end if;
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
