CREATE OR REPLACE FUNCTION san_qtd_doacao_rel4722 (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qtd_w  			varchar(10);
ds_retorno_w		varchar(20);

BEGIN

select 	count(*)
into STRICT	qtd_w
from 	san_doacao
where 	cd_pessoa_fisica = cd_pessoa_fisica_p;


if (qtd_w < 10) then
	ds_retorno_w := 0||qtd_w;
else
	ds_retorno_w := qtd_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
