CREATE OR REPLACE FUNCTION san_qt_concentr_leucometria ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


vl_retorno_w		varchar(4000);
ie_tipo_coleta_w	smallint;
qt_coletada_w		smallint;
qt_leucometria_c1_w	bigint;



BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(a.ie_tipo_coleta),
		max(a.qt_coletada),
		max(b.qt_leucometria_c1)
	into STRICT 	ie_tipo_coleta_w,
		qt_coletada_w,
		qt_leucometria_c1_w
	from	san_doacao a,
		san_leucometria b
	WHERE	a.nr_sequencia = b.nr_seq_doacao
	AND	b.ie_leucometria_padrao = 'S'
	AND	b.nr_seq_producao = nr_sequencia_p;

	if (ie_tipo_coleta_w IS NOT NULL AND ie_tipo_coleta_w::text <> '') then
		if ( ie_tipo_coleta_w = '4') then
			SELECT	round((qt_coletada_w/6)::numeric,2)
			into STRICT	vl_retorno_w
			;
		else
			if 	((qt_coletada_w*qt_leucometria_c1_w) < (qt_coletada_w)) then

				select	round(((qt_coletada_w/qt_leucometria_c1_w) - qt_coletada_w),2)
				into STRICT	vl_retorno_w
				;
			else
				select 	round(((qt_coletada_w - (qt_coletada_w/qt_leucometria_c1_w))-((5*((qt_coletada_w/qt_leucometria_c1_w) - qt_coletada_w))/20)),2)
				into STRICT	vl_retorno_w
				;
			end if;
		end if;
	end if;
end if;
return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
