SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_qt_doador_pendente ( nr_seq_reposicao_p bigint ) RETURNS bigint AS $body$
DECLARE


retorno_w		bigint;
qt_total_doador_w  	bigint;
qt_repo_w    		bigint;


BEGIN

SELECT	coalesce(SUM(coalesce(qt_doacao, 1)),0)
INTO STRICT 	qt_total_doador_w
FROM 	san_reposicao_doador
WHERE 	nr_seq_reposicao = nr_seq_reposicao_p;

select 	qt_reposicao
into STRICT 	qt_repo_w
from  	san_reposicao
where 	nr_sequencia = nr_seq_reposicao_p;

if (qt_repo_w >= qt_total_doador_w ) then
	retorno_w := qt_repo_w - qt_total_doador_w;
else
	retorno_w := 0;
end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
