SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_restricoes_frac (nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_restricao_w		varchar(50);
ds_retorno_w		varchar(2000);
ds_separador_w		varchar(2);

c01 CURSOR FOR
	SELECT  substr(obter_desc_san_derivado(b.nr_seq_derivado),1,50)
		from	san_questionario a,
			san_bloq_frac_impedimento b,
			san_impedimento x
		where	x.nr_sequencia 		= a.nr_seq_impedimento
		and	a.nr_seq_impedimento	= b.nr_seq_impedimento
		and	a.ie_resposta 		<> coalesce(x.ie_resposta_correta,'N')
		and 	(a.ie_resposta IS NOT NULL AND a.ie_resposta::text <> '')
		and	a.nr_seq_doacao 	= nr_seq_doacao_p;


BEGIN
ds_restricao_w 	:= '';
ds_retorno_w 	:= '';
ds_separador_w 	:= '';

open c01;
loop
fetch c01 into
	ds_restricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		if (length(ds_retorno_w || ds_separador_w || trim(both ds_restricao_w)) <= 2000) then
			ds_retorno_w := ds_retorno_w || ds_separador_w || trim(both ds_restricao_w);
			ds_separador_w := ', ';
		end if;
	end;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION san_restricoes_frac (nr_seq_doacao_p bigint) FROM PUBLIC;

