CREATE OR REPLACE FUNCTION san_result_exame_procergs ( nr_seq_doacao_p bigint, ie_tipo_exame_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(3) := 'NAR';
nr_seq_exame_lote_w	san_exame_lote.nr_sequencia%type;
ds_resultado_w		san_exame_realizado.ds_resultado%type;

BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') and (ie_tipo_exame_p IS NOT NULL AND ie_tipo_exame_p::text <> '') then

	select	max(a.nr_sequencia)
	into STRICT	nr_seq_exame_lote_w
	from	san_exame_lote a
	where	a.nr_seq_doacao = nr_seq_doacao_p;

	if (nr_seq_exame_lote_w IS NOT NULL AND nr_seq_exame_lote_w::text <> '') then

		select	max(a.ds_resultado)
		into STRICT	ds_resultado_w
		from	san_exame_realizado a,
			san_exame b
		where	a.nr_seq_exame_lote	= nr_seq_exame_lote_w
		and	a.nr_seq_exame 		= b.nr_sequencia
		and	b.ie_tipo_exame		= ie_tipo_exame_p
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');

		if (ds_resultado_w IS NOT NULL AND ds_resultado_w::text <> '') then

			select	CASE WHEN coalesce(a.ds_resultado::text, '') = '' THEN  'NAR'  ELSE CASE WHEN b.cd_tipo_resultado=1356 THEN  CASE WHEN upper(a.ds_resultado)='POSITIVO' THEN  'POS' WHEN upper(a.ds_resultado)='NEGATIVO' THEN  'NEG'  ELSE CASE WHEN ie_tipo_exame_p='2' THEN  'INC'  ELSE 'NAR' END  END   WHEN b.cd_tipo_resultado=1357 THEN  CASE WHEN upper(a.ds_resultado)='REAGENTE' THEN  'POS' WHEN upper(a.ds_resultado)='N√ÉO REAGENTE' THEN  'NEG'  ELSE CASE WHEN ie_tipo_exame_p='2' THEN  'INC'  ELSE 'NAR' END  END   ELSE san_obter_de_para_integracao('RES', c.nr_sequencia) END  END
			into STRICT	ds_retorno_w
			FROM san_exame b, san_exame_realizado a
LEFT OUTER JOIN san_exame_valor c ON (a.nr_seq_exame = c.nr_seq_exame AND a.ds_resultado = c.ds_valor)
WHERE a.nr_seq_exame_lote	= nr_seq_exame_lote_w and a.nr_seq_exame 		= b.nr_sequencia and b.ie_tipo_exame		= ie_tipo_exame_p   and a.ds_resultado		= ds_resultado_w and (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')   LIMIT 1;

		end if;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
