CREATE OR REPLACE FUNCTION san_se_bloqueia_exames_valores ( nr_seq_doacao_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_exame_lote_w	bigint;
ie_apto_amostra_w	varchar(1);
ds_retorno_w      	varchar(1) := 'N';

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	san_exame_lote
	where	nr_seq_doacao	= nr_seq_doacao_p;


BEGIN

select	coalesce(max(ie_apto_amostra),'N')
	into STRICT	ie_apto_amostra_w
from	san_doacao
where	nr_sequencia = nr_seq_doacao_p;

if ( ie_apto_amostra_w = 'N' ) then

	open c01;
	loop
	fetch c01 into
		nr_seq_exame_lote_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select COALESCE(max('S'), 'N')
			into STRICT ds_retorno_w
		from san_exame_realizado x,
		san_exame a,
		san_exame_valor b
		where a.nr_sequencia = x.nr_seq_exame
		and x.nr_seq_exame = b.nr_seq_exame
		and x.nr_seq_exame_lote = nr_seq_exame_lote_w
		and x.ds_resultado = to_char(b.nr_sequencia)
		and b.ie_bloq_sorologia = 'S'
		and coalesce(a.cd_tipo_resultado::text, '') = '';

		end;
		
		if ( ds_retorno_w = 'S' ) then
			EXIT;
		end if;

	end loop;
	close c01;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
