CREATE OR REPLACE FUNCTION san_status_gerar_solic (nr_seq_derivado_p bigint, qt_estoque_p bigint, ie_tipo_sangue_p text, ie_fator_rh_p text) RETURNS varchar AS $body$
DECLARE


qt_estoque_min_w	integer;
qt_estoque_max_w	integer;
ds_retorno_w		varchar(2);

BEGIN

if (nr_seq_derivado_p IS NOT NULL AND nr_seq_derivado_p::text <> '') and (qt_estoque_p >= 0) and (ie_tipo_sangue_p IS NOT NULL AND ie_tipo_sangue_p::text <> '') and (ie_fator_rh_p IS NOT NULL AND ie_fator_rh_p::text <> '') then

	select	max(qt_estoque_min),
		max(qt_estoque_max)
	into STRICT	qt_estoque_min_w,
		qt_estoque_max_w
	from	san_gerar_solic_hemoc
	where	nr_seq_derivado = nr_seq_derivado_p
	and	ie_tipo_sangue  = ie_tipo_sangue_p
	and	ie_fator_rh	= ie_fator_rh_p;

	if (qt_estoque_min_w IS NOT NULL AND qt_estoque_min_w::text <> '') and (qt_estoque_max_w IS NOT NULL AND qt_estoque_max_w::text <> '') then

		if (qt_estoque_p < qt_estoque_min_w) then
			ds_retorno_w := 'MI';
		elsif (qt_estoque_p between qt_estoque_min_w and qt_estoque_max_w) then
			ds_retorno_w := 'E';
		elsif (qt_estoque_p > qt_estoque_max_w) then
			ds_retorno_w := 'MA';
		end if;
	end if;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
