CREATE OR REPLACE FUNCTION san_tipo_sangue_reserva ( cd_pessoa_fisica_p text, ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_exame_rh_param_w	bigint;
nr_seq_exame_tipo_param_w	bigint;
ie_atualizar_tipo_sangue_w varchar(1);
ds_retorno_w		varchar(20);


BEGIN
SELECT 	max(nr_seq_exame_RH),
	max(nr_seq_exame_tipo),
	max(ie_atualizar_tipo_sangue)
into STRICT	nr_seq_exame_rh_param_w,
	nr_seq_exame_tipo_param_w,
	ie_atualizar_tipo_sangue_w
FROM 	san_parametro
where	cd_estabelecimento = cd_estabelecimento_p;

if (ie_atualizar_tipo_sangue_w = 'S') then
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_opcao_p = 'FH') then
	SELECT  	MAX(ds_resultado)
	into STRICT	ds_retorno_w
	FROM 	san_exame_realizado a,
		san_exame_lote b,
		san_reserva c
	WHERE 	a.nr_seq_exame_lote 	= b.nr_sequencia
	AND	b.nr_seq_reserva	= c.nr_sequencia
	AND	c.cd_pessoa_fisica  	= cd_pessoa_fisica_p
	AND	a.nr_seq_exame	  	= nr_seq_exame_rh_param_w
	AND	a.nr_seq_exame_lote 	= ( 	SELECT  MAX(a.nr_seq_exame_lote)
						FROM 	san_exame_realizado a,
							san_exame_lote b,
							san_reserva c
						WHERE 	a.nr_seq_exame_lote = b.nr_sequencia
						AND	b.nr_seq_reserva	= c.nr_sequencia
						AND	c.cd_pessoa_fisica  	= cd_pessoa_fisica_p
						AND	a.nr_seq_exame	  	= nr_seq_exame_rh_param_w);
	end if;

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (ie_opcao_p = 'TS') then
	SELECT 	substr(MAX(ds_resultado),1,2)
	into STRICT	ds_retorno_w
	FROM 	san_exame_realizado a,
		san_exame_lote b,
		san_reserva c
	WHERE 	a.nr_seq_exame_lote 	= b.nr_sequencia
	AND	b.nr_seq_reserva	= c.nr_sequencia
	AND	c.cd_pessoa_fisica  	= cd_pessoa_fisica_P
	AND	a.nr_seq_exame	  	= nr_seq_exame_tipo_param_w
	AND	a.nr_seq_exame_lote 	= ( 	SELECT  MAX(a.nr_seq_exame_lote)
						FROM 	san_exame_realizado a,
							san_exame_lote b,
							san_reserva c
						WHERE 	a.nr_seq_exame_lote = b.nr_sequencia
						AND	b.nr_seq_reserva	= c.nr_sequencia
						AND	c.cd_pessoa_fisica  	= cd_pessoa_fisica_P
						AND	a.nr_seq_exame	  	= nr_seq_exame_tipo_param_w);
	end if;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
