SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_ultimo_fenotipo_pf ( cd_pessoa_fisica_p text, ie_doador_p text, ie_padrao_p bigint default 0) RETURNS varchar AS $body$
DECLARE


nr_seq_fenotipagem_w	bigint;			
ds_fenotipo_w		varchar(255);


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	if (ie_doador_p = 'N') then
		select max(seq)
			into STRICT	nr_seq_fenotipagem_w
		from (
			SELECT	nr_sequencia seq,
				a.dt_liberacao dtlib, 
				a.dt_inativacao dtint
			from 	san_result_fenotipagem a
			where 	a.nr_seq_reserva IN (
					SELECT 	b.nr_sequencia
					from 	san_reserva b 
					where cd_pessoa_fisica = cd_pessoa_fisica_p)
			
union

			select	nr_sequencia, 
				a.dt_liberacao dtlib, 
				a.dt_inativacao dtint
			from 	san_result_fenotipagem a
			where 	a.nr_seq_transfusao IN (
					select	c.nr_sequencia
					from	san_transfusao c 
					where	cd_pessoa_fisica = cd_pessoa_fisica_p)) alias5
		where 	(dtlib IS NOT NULL AND dtlib::text <> '')
		and	coalesce(dtint::text, '') = '';

	elsif (ie_doador_p = 'S') then
		select max(seq)
			into STRICT	nr_seq_fenotipagem_w
		from (
			SELECT	nr_sequencia seq,
				a.dt_liberacao dtlib, 
				a.dt_inativacao dtint
			from 	san_result_fenotipagem a
			where 	a.nr_seq_doacao IN (
					SELECT 	b.nr_sequencia
					from 	san_doacao b 
					where cd_pessoa_fisica = cd_pessoa_fisica_p)
			
union

			select	nr_sequencia, 
				a.dt_liberacao dtlib, 
				a.dt_inativacao dtint
			from 	san_result_fenotipagem a
			where 	a.nr_seq_producao IN (
					select	c.nr_sequencia
					from	san_producao c 
					where	cd_pessoa_fisica = cd_pessoa_fisica_p)) alias4
		where 	(dtlib IS NOT NULL AND dtlib::text <> '')
		and	coalesce(dtint::text, '') = '';
	end if;	
end if;

if (nr_seq_fenotipagem_w IS NOT NULL AND nr_seq_fenotipagem_w::text <> '') then
	if ( ie_padrao_p = 0 ) then
		select	substr(san_obter_result_fenotipagem(nr_seq_fenotipagem_w),1,255)
		into STRICT	ds_fenotipo_w
		;
	else
		select	substr(san_obter_fenotipagem_padrao(nr_seq_fenotipagem_w),1,255)
		into STRICT	ds_fenotipo_w
		;
	end if;
end if;

return	ds_fenotipo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
