CREATE OR REPLACE FUNCTION san_ultimo_fenotipo_prod (nr_seq_producao_p text, ie_padrao_p bigint default 0) RETURNS varchar AS $body$
DECLARE


nr_seq_fenotipagem_w	bigint;			
ds_fenotipo_w		varchar(255);


BEGIN
if (nr_seq_producao_p IS NOT NULL AND nr_seq_producao_p::text <> '') then
		select	Max(nr_sequencia)
		into STRICT	nr_seq_fenotipagem_w
		from 	san_result_fenotipagem a
		where 	a.nr_seq_producao = nr_seq_producao_p
        and     (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
        and     coalesce(a.dt_inativacao::text, '') = '';

end if;
if (nr_seq_fenotipagem_w IS NOT NULL AND nr_seq_fenotipagem_w::text <> '') then
	if ( ie_padrao_p = 0 ) then
		select	substr(san_obter_result_fenotipagem(nr_seq_fenotipagem_w),1,255)
			into STRICT	ds_fenotipo_w
		;
	else
		select	substr(san_obter_fenotipagem_padrao(nr_seq_fenotipagem_w),1,255)
			into STRICT	ds_fenotipo_w
		;
	end if;
end if;

return	ds_fenotipo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
