SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_valida_volume_doacao ( nr_seq_doacao_p bigint, nr_seq_hemocomponente_p bigint, qt_volume_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_bolsa_doacao_w		san_doacao.ie_tipo_bolsa%type;
nr_seq_derivado_doacao_w	san_doacao.nr_seq_derivado%type;
nr_seq_regra_w			san_derivado_reproducao.nr_sequencia%type;
ie_possui_regra_w		varchar(1) := 'N';
qt_volume_mim_w			bigint;
qt_volume_max_w			bigint;
ds_retorno_w			varchar(1) := 'N';


BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	select	max(ie_tipo_bolsa),
		max(nr_seq_derivado_padrao)
	into STRICT	ie_tipo_bolsa_doacao_w,
		nr_seq_derivado_doacao_w
	from	san_doacao
	where	nr_sequencia = nr_seq_doacao_p;


	if (nr_seq_derivado_doacao_w IS NOT NULL AND nr_seq_derivado_doacao_w::text <> '') then

		select 	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_possui_regra_w
		from	san_derivado_reproducao
		where	nr_seq_derivado = nr_seq_derivado_doacao_w
		and	ie_tipo_bolsa = ie_tipo_bolsa_doacao_w
		and	ie_situacao = 'A';

		if (ie_possui_regra_w = 'S') then

			select 	max(a.nr_sequencia),
				max(a.qt_volume_min),
				max(a.qt_volume_max)
			into STRICT	nr_seq_regra_w,
				qt_volume_mim_w,
				qt_volume_max_w
			from	san_derivado_reproducao a
			where	a.nr_seq_derivado = nr_seq_derivado_doacao_w
			and (coalesce(a.ie_tipo_bolsa, 0) = coalesce(ie_tipo_bolsa_doacao_w, 0)
				or coalesce(a.ie_tipo_bolsa::text, '') = '')
			and	a.nr_seq_derivado_rep = nr_seq_hemocomponente_p
			and	a.ie_situacao = 'A';

			if (qt_volume_mim_w IS NOT NULL AND qt_volume_mim_w::text <> '') or (qt_volume_max_w IS NOT NULL AND qt_volume_max_w::text <> '') then

				select 	coalesce(max('N'),'S')
				into STRICT	ds_retorno_w
				from	san_derivado_reproducao a
				where	a.nr_sequencia = nr_seq_regra_w
				and	coalesce(qt_volume_doacao_p, 0) between coalesce(qt_volume_mim_w, 0) and coalesce(qt_volume_max_w, 9999);

			end if;

		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
