CREATE OR REPLACE FUNCTION san_valida_volume_total_prod ( nr_seq_derivado_origem_p bigint, nr_seq_producao_p bigint, qt_volume_p bigint, nr_seq_doacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_volume_total_prod_w	bigint := 0;
nr_seq_derivado_rep_w	bigint;
qt_volume_derivado_w	smallint;
ie_tipo_bolsa_w		san_doacao.ie_tipo_bolsa%type;
qt_volume_real_w	san_doacao.qt_volume_real%type;

C01 CURSOR FOR
	SELECT	distinct nr_seq_derivado_rep
	from	san_derivado_reproducao
	where	nr_seq_derivado = nr_seq_derivado_origem_p
	and (coalesce(ie_tipo_bolsa,0) = coalesce(ie_tipo_bolsa_w,0)
	or (coalesce(ie_tipo_bolsa::text, '') = ''))
	and	(nr_seq_motivo_inut IS NOT NULL AND nr_seq_motivo_inut::text <> '')
	and	ie_situacao = 'A';

BEGIN

select	max(ie_tipo_bolsa),
	max(qt_volume_real)
into STRICT	ie_tipo_bolsa_w,
	qt_volume_real_w
from	san_doacao
where	nr_sequencia = nr_seq_doacao_p;

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_seq_derivado_rep_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		select	sum(qt_volume)
		into STRICT	qt_volume_derivado_w
		from	san_producao
		where	nr_seq_derivado = nr_seq_derivado_rep_w
		and	nr_sequencia <> nr_seq_producao_p
		and	nr_seq_doacao = nr_seq_doacao_p
		and	coalesce(dt_inutilizacao::text, '') = '';

		qt_volume_total_prod_w := qt_volume_total_prod_w + coalesce(qt_volume_derivado_w, 0);

		end;
	end loop;
	close C01;

	qt_volume_total_prod_w := qt_volume_total_prod_w + coalesce(qt_volume_p, 0);

	if (coalesce(qt_volume_total_prod_w,0) > coalesce(qt_volume_real_w,0)) then
		ds_retorno_w := 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
