CREATE OR REPLACE FUNCTION san_valida_vol_prod_regra ( nr_seq_doacao_p bigint, nr_seq_derivado_origem_p bigint, nr_seq_hemocomponente_p bigint, qt_volume_p bigint) RETURNS bigint AS $body$
DECLARE


ie_tipo_bolsa_doacao_w		san_doacao.ie_tipo_bolsa%type;
nr_seq_regra_w			san_derivado_reproducao.nr_sequencia%type;
ie_possui_regra_w		varchar(1) := 'N';
qt_volume_mim_w			bigint;
qt_volume_max_w			bigint;
ie_fora_faixa_w			varchar(1) := 'N';
nr_seq_motivo_inut_w		san_motivo_inutil.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	san_derivado_reproducao a
	where	a.nr_seq_derivado = nr_seq_derivado_origem_p
	and (coalesce(a.ie_tipo_bolsa, 0) = coalesce(ie_tipo_bolsa_doacao_w, 0)
		or coalesce(a.ie_tipo_bolsa::text, '') = '')
	and	a.nr_seq_derivado_rep = nr_seq_hemocomponente_p
	and	(a.nr_seq_motivo_inut IS NOT NULL AND a.nr_seq_motivo_inut::text <> '')
	and	a.ie_situacao = 'A'
	order by 1;


BEGIN

if (nr_seq_doacao_p IS NOT NULL AND nr_seq_doacao_p::text <> '') then

	select	max(ie_tipo_bolsa)
	into STRICT	ie_tipo_bolsa_doacao_w
	from	san_doacao
	where	nr_sequencia = nr_seq_doacao_p;


	if (nr_seq_derivado_origem_p IS NOT NULL AND nr_seq_derivado_origem_p::text <> '') then

		select 	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ie_possui_regra_w
		from	san_derivado_reproducao
		where	nr_seq_derivado = nr_seq_derivado_origem_p
		and (coalesce(ie_tipo_bolsa, 0) = coalesce(ie_tipo_bolsa_doacao_w, 0)
			or coalesce(ie_tipo_bolsa::text, '') = '')
		and	(nr_seq_motivo_inut IS NOT NULL AND nr_seq_motivo_inut::text <> '')
		and	ie_situacao = 'A';

		if (ie_possui_regra_w = 'S') then

			open C01;
			loop
			fetch C01 into
				nr_seq_regra_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin

				if (ie_fora_faixa_w = 'N') then
					select 	max(a.nr_seq_motivo_inut)
					into STRICT	nr_seq_motivo_inut_w
					from	san_derivado_reproducao a
					where 	a.nr_sequencia = nr_seq_regra_w;

					select 	coalesce(max('N'),'S')
					into STRICT	ie_fora_faixa_w
					from	san_derivado_reproducao a
					where	a.nr_sequencia = nr_seq_regra_w
					and	coalesce(qt_volume_p, 0) between coalesce(a.qt_volume_min, 0) and coalesce(a.qt_volume_max, 9999)
					and ((a.qt_volume_min IS NOT NULL AND a.qt_volume_min::text <> '') or (a.qt_volume_max IS NOT NULL AND a.qt_volume_max::text <> ''));
				end if;

				end;
			end loop;
			close C01;

		end if;

	end if;
end if;

if (ie_fora_faixa_w = 'S') then
	return nr_seq_motivo_inut_w;
else
	return null;
end if;

end;
$body$
