SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION san_verificar_regra_producao ( nr_seq_derivado_p bigint) RETURNS varchar AS $body$
DECLARE


/*
C = Conservante
M = Material
A = Ambos
*/
ds_retorno_w	varchar(1);

BEGIN

if (nr_seq_derivado_p IS NOT NULL AND nr_seq_derivado_p::text <> '') then

	SELECT 	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'C' END
	into STRICT	ds_retorno_w
	FROM	san_derivado_regra
	WHERE  	nr_seq_derivado = nr_seq_derivado_p
	AND	(nr_seq_conservante IS NOT NULL AND nr_seq_conservante::text <> '')
	and	ie_situacao = 'A'
	and not exists (SELECT 1
			from	san_derivado_regra
			WHERE  	nr_seq_derivado = nr_seq_derivado_p
			AND	(cd_material IS NOT NULL AND cd_material::text <> '')
			and	ie_situacao = 'A');

	if (ds_retorno_w = 'N') then
		SELECT	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'M' END
		into STRICT	ds_retorno_w
		FROM   	san_derivado_regra
		WHERE  	nr_seq_derivado = nr_seq_derivado_p
		AND	(cd_material IS NOT NULL AND cd_material::text <> '')
		and	ie_situacao = 'A'
		and not exists (SELECT 1
			from	san_derivado_regra
			WHERE  	nr_seq_derivado = nr_seq_derivado_p
			AND	(nr_seq_conservante IS NOT NULL AND nr_seq_conservante::text <> '')
			and	ie_situacao = 'A');
	end if;

	if (ds_retorno_w = 'N') then
		SELECT	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'A' END
		into STRICT	ds_retorno_w
		FROM   	san_derivado_regra
		WHERE  	nr_seq_derivado = nr_seq_derivado_p
		and	ie_situacao = 'A'
		and exists (SELECT 1
			from	san_derivado_regra
			WHERE  	nr_seq_derivado = nr_seq_derivado_p
			AND	(cd_material IS NOT NULL AND cd_material::text <> '')
			and	ie_situacao = 'A')
		and exists (select 1
			from	san_derivado_regra
			WHERE  	nr_seq_derivado = nr_seq_derivado_p
			AND	(nr_seq_conservante IS NOT NULL AND nr_seq_conservante::text <> '')
			and	ie_situacao = 'A');
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
