CREATE OR REPLACE FUNCTION san_verifica_barras_envio ( nr_seq_envio_p bigint, cd_barras_p text, ie_barras_p text) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w	varchar(1);

BEGIN
 
if (nr_seq_envio_p IS NOT NULL AND nr_seq_envio_p::text <> '') then 
 
if (ie_barras_p = 'H') then 
	SELECT	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_retorno_w 
	FROM 	san_producao_v a,   
		san_envio_derivado_val x, 	 		 
		san_envio_derivado z    
	WHERE	z.nr_sequencia 	  = x.nr_seq_envio  
	AND	x.nr_seq_producao = a.nr_sequencia 
	AND	a.nr_sequencia	  = x.nr_seq_producao  
	and	z.nr_sequencia	  = nr_seq_envio_p 
	AND 	coalesce(a.cd_barras, a.cd_barras_integracao||REPLACE(TO_CHAR(nr_seq_derivado,'00'),' ','')) = cd_barras_p;
elsif (ie_barras_p = 'N') then 
	SELECT	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_retorno_w 
	FROM 	san_envio_derivado_val x, 	 		 
		san_envio_derivado z    
	WHERE	z.nr_sequencia 	  = x.nr_seq_envio  
	AND	x.nr_seq_producao = (cd_barras_p)::numeric  
	and	z.nr_sequencia	  = nr_seq_envio_p;
end if;
 
end if;
 
return	ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
