CREATE OR REPLACE FUNCTION san_verifica_se_desistiu (nr_seq_desistencia_p bigint) RETURNS varchar AS $body$
DECLARE

ie_paciente_desistiu_w	varchar(1);


BEGIN

if (nr_seq_desistencia_p IS NOT NULL AND nr_seq_desistencia_p::text <> '') then
	select	max(ie_desistencia)
	into STRICT	ie_paciente_desistiu_w
	from	san_motivo_desistencia
	where	nr_sequencia = nr_seq_desistencia_p
	and	ie_situacao = 'A';
end if;
return	ie_paciente_desistiu_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
