CREATE OR REPLACE FUNCTION san_verif_result_exame_ant ( cd_pessoa_fisica_p text, ds_resultado_exame_p text, nr_seq_doacao_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(5) := 'N';
ds_result_exame_ant_w	varchar(100);
ds_result_exame_atual_w	varchar(100);
ie_verif_result_exame_w	varchar(1);
nr_seq_doacao_w		bigint;
nr_seq_exame_lote_w	bigint;


BEGIN
if (nr_seq_exame_p IS NOT NULL AND nr_seq_exame_p::text <> '') then
	ds_result_exame_atual_w := ds_resultado_exame_p;

	select	x.ie_consiste_result_ant
	into STRICT	ie_verif_result_exame_w
	from	san_exame x
	where	x.nr_sequencia = nr_seq_exame_p;


	if (ie_verif_result_exame_w = 'S') and (ds_result_exame_atual_w IS NOT NULL AND ds_result_exame_atual_w::text <> '') then

		select	max(nr_sequencia)
		into STRICT	nr_seq_doacao_w
		from	san_doacao
		where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento
		and	nr_sequencia <> nr_seq_doacao_p
		and	cd_pessoa_fisica = cd_pessoa_fisica_p;

		select	max(z.nr_sequencia)
		into STRICT	nr_seq_exame_lote_w
		from	san_exame_lote z
		where	z.nr_seq_doacao = nr_seq_doacao_w;

		select	max(ds_resultado)
		into STRICT	ds_result_exame_ant_w
		from	san_exame_realizado x
		where	x.nr_seq_exame = nr_seq_exame_p
		and	x.nr_seq_exame_lote = nr_seq_exame_lote_w;

		if (lower(ds_result_exame_atual_w) <> lower(ds_result_exame_ant_w)) then
			ds_retorno_w := 'S';
		end if;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
