SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sath_get_adds_score ( nr_atendimento_p bigint, qt_horas_p bigint, nr_seq_sinal_vital_p bigint default null) RETURNS varchar AS $body$
DECLARE

			
dt_inicio_w	timestamp;			
dt_fim_w	timestamp	:= clock_timestamp();
qt_idade_w	bigint;
nr_seq_adds_w	bigint;

C01 CURSOR FOR
	SELECT 	nr_sequencia ,
		nm_modelo
	from 	adds_modelo
	where 	qt_idade_w between coalesce(qt_idade_min,0) and coalesce(qt_idade_max,999)
	and 	coalesce(ie_situacao,'A') ='A'
	and 	obter_se_exibe_adds(nr_sequencia, nr_atendimento_p ) = 'S'
	order by 2;


C02 CURSOR FOR
	SELECT *
	from table(adds_pck.obter_score_total_data(nr_atendimento_p,nr_seq_adds_w,dt_inicio_w,dt_fim_w,'N'));


ie_maior_score_w	bigint;

BEGIN

if (obter_funcao_ativa <> 281) then
	return null;
end if;



if (nr_seq_sinal_vital_p IS NOT NULL AND nr_seq_sinal_vital_p::text <> '') then
	select	max(a.dt_sinal_vital)
	into STRICT	dt_inicio_w
	from	atendimento_sinal_vital a
	where	nr_sequencia = nr_seq_sinal_vital_p;

	dt_fim_w	:=dt_inicio_w;

else
	dt_inicio_w	:= dt_fim_w - ((1/24)*qt_horas_p);

end if;

select	coalesce(max(obter_idade_pf(a.cd_pessoa_fisica,clock_timestamp(),'A')),0)
into STRICT	qt_idade_w
from	atendimento_paciente a
where	a.nr_atendimento	= nr_atendimento_p;


for r_c01 in c01 loop
	begin
	nr_seq_adds_w	:= r_c01.nr_sequencia;
	exit;
	end;
end loop;

if (nr_seq_adds_w IS NOT NULL AND nr_seq_adds_w::text <> '') then

	for r_c02 in c02 loop
		begin

		if (r_c02.ie_score	=	'E') then
			return r_c02.ie_score;
		else
			if ((r_c02.ie_score)::numeric 	> coalesce(ie_maior_score_w,0)) then
				ie_maior_score_w	:= (r_c02.ie_score)::numeric;
				if (ie_maior_score_w >=8) then
				     return 'E';
				end if;
			end if;
		end if;

		end;
	end loop;
end if;




return	ie_maior_score_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
