CREATE OR REPLACE FUNCTION sa_obter_se_regra_turno_lib (nr_seq_horario_p bigint, nr_seq_classif_agenda_item_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_classif_agend_w 	agenda_horario.nr_seq_classif_agenda%type;
qt_regra_w	smallint;
ie_regra_w varchar(1);


BEGIN

ie_regra_w := 'N';

select	max(nr_seq_classif_agenda) nr_seq_classif_agend
		into STRICT nr_seq_classif_agend_w
from  	agenda_horario
where 	nr_sequencia = nr_seq_horario_p;

if (nr_seq_classif_agend_w > 0) then
	select	count(1)
	into STRICT	qt_regra_w
	from	agenda_turno_classif_lib
	where 	nr_seq_horario = nr_seq_horario_p;
	
	if (qt_regra_w > 0)then
		select	count(1)
		into STRICT 	qt_regra_w
		from  	agenda_turno_classif_lib
		where 	nr_seq_classif_agenda = nr_seq_classif_agenda_item_p
		and   	nr_seq_horario = nr_seq_horario_p;
		
		if (qt_regra_w > 0) then
			ie_regra_w :=  'S';
		end if;
	end if;
end if;


return	ie_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
