CREATE OR REPLACE FUNCTION schematic_get_parent_wdlg ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequence_w			objeto_schematic.nr_sequencia%type := 0;
nr_seq_obj_parent_w		objeto_schematic.nr_seq_obj_sup%type := 0;
ie_type_object_w		objeto_schematic.ie_tipo_objeto%type;
ie_type_object_parent_w		objeto_schematic.ie_tipo_objeto%type;
ie_type_component_parent_w	objeto_schematic.ie_tipo_componente%type;


BEGIN

select	nr_seq_obj_sup,
	ie_tipo_objeto
into STRICT	nr_seq_obj_parent_w,
	ie_type_object_w
from	objeto_schematic
where	nr_sequencia	= nr_sequencia_p;

select	ie_tipo_objeto,
	ie_tipo_componente,
	nr_sequencia
into STRICT	ie_type_object_parent_w,
	ie_type_component_parent_w,
	nr_sequence_w
from	objeto_schematic
where	nr_sequencia	= nr_seq_obj_parent_w;

if (ie_type_component_parent_w = 'WDLG') then
	return	nr_sequence_w;
else
	select	schematic_get_parent_wdlg(nr_seq_obj_parent_w)
	into STRICT	nr_sequence_w
	;

	return nr_sequence_w;
end if;

end;
$body$
