SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION schematic_obter_se_exibe_comp (nr_seq_objeto_sup_p bigint, ie_tipo_objeto_p text, nr_seq_tipo_schematic_p bigint, ie_tipo_componente_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_w		bigint;
nr_seq_regra_obj_w	bigint;
qt_regra_w		bigint;
ie_permite_w		varchar(10);
ie_tipo_objeto_w	varchar(15);
nr_seq_tipo_schematic_w	bigint;


BEGIN

ie_permite_w	:= 'N';

select	max(nr_sequencia)
into STRICT	nr_seq_regra_w
from	regra_schematic
where	ie_situacao 	= 'A'
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

select	max(ie_tipo_objeto),
	max(nr_seq_tipo_schematic)
into STRICT	ie_tipo_objeto_w,
	nr_seq_tipo_schematic_w
from	objeto_schematic
where	nr_sequencia	= nr_seq_objeto_sup_p;

ie_tipo_objeto_w	:= coalesce(ie_tipo_objeto_p,ie_tipo_objeto_w);
nr_seq_tipo_schematic_w	:= coalesce(nr_seq_tipo_schematic_p,nr_seq_tipo_schematic_w);

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') and (ie_tipo_objeto_w IS NOT NULL AND ie_tipo_objeto_w::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_regra_obj_w
	from	regra_schematic_obj a
	where	a.nr_seq_regra		= nr_seq_regra_w
	and	a.ie_tipo_objeto	= ie_tipo_objeto_w
	and	a.nr_seq_tipo_schematic	= nr_seq_tipo_schematic_w;


	if (coalesce(nr_seq_regra_obj_w,0) > 0) then
		select	count(*)
		into STRICT	qt_regra_w
		from	regra_schematic_obj_filho
		where	nr_seq_regra_obj	= nr_seq_regra_obj_w
		and	ie_tipo_objeto		= 'C'
		and	ie_tipo_componente	= ie_tipo_componente_p
		and	coalesce(nr_seq_tipo_schematic,nr_seq_tipo_schematic_w)	= nr_seq_tipo_schematic_w;

	else
		select	count(*)
		into STRICT	qt_regra_w
		from	regra_schematic_obj a
		where	a.nr_seq_regra		= nr_seq_regra_w
		and	a.ie_tipo_objeto	= ie_tipo_objeto_w
		and	coalesce(a.nr_seq_tipo_schematic::text, '') = ''
		and	exists (SELECT	1
				from	regra_schematic_obj_filho b
				where	b.nr_seq_regra_obj	= a.nr_sequencia
				and	b.ie_tipo_objeto	= 'C'
				and	coalesce(b.ie_tipo_componente,ie_tipo_componente_p)	= ie_tipo_componente_p
				and	coalesce(b.nr_seq_tipo_schematic,coalesce(nr_seq_tipo_schematic_w,0)) = coalesce(nr_seq_tipo_schematic_w,0));
	end if;


	if (qt_regra_w > 0) then
		ie_permite_w	:= 'S';
	end if;
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
