CREATE OR REPLACE FUNCTION schematic_obter_se_filho_conf (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_configuravel_w	varchar(10);
nr_sequencia_w		bigint;


BEGIN

begin
select	nr_sequencia,
	coalesce(ie_configuravel,'N')
into STRICT	nr_sequencia_w,
	ie_configuravel_w
from	objeto_schematic
where	nr_seq_obj_sup	= nr_sequencia_p  LIMIT 1;
exception
when others then
	nr_sequencia_w := null;
	ie_configuravel_w := 'N';
end;

if (coalesce(nr_sequencia_w::text, '') = '') then
	return 'N';
elsif (ie_configuravel_w = 'S') then
	return 'S';
else
	return SCHEMATIC_OBTER_SE_FILHO_CONF(nr_sequencia_w);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
