CREATE OR REPLACE FUNCTION schematic_obter_tipo_objeto ( nr_sequencia_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ie_tipo_objeto_w 	varchar(15);
nr_seq_obj_sup_w 	bigint;
nr_sequencia_w 		bigint;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	nr_sequencia_w := nr_sequencia_p;

	if (ie_tipo_p = 'SUP')  then
		begin
		select 	max(nr_seq_obj_sup)
		into STRICT	nr_sequencia_w
		from	objeto_schematic
		where	nr_sequencia = nr_sequencia_p;
		end;
	end if;

	select 	ie_tipo_objeto
	into STRICT	ie_tipo_objeto_w
	from	objeto_schematic
	where 	nr_sequencia = nr_sequencia_w;
	end;
end if;

return	ie_tipo_objeto_w;

end;
$body$
LANGUAGE PLPGSQL
