CREATE OR REPLACE FUNCTION sepaco_obter_diarias (nr_atendimento_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*ie_opcao
	'T' Todas
	'F' Faturadas'*/
vl_retorno_w	double precision := 0;


BEGIN

if (ie_opcao_p = 'T') then
		select	count(*)
		into STRICT	vl_retorno_w
		from	atendimento_paciente;
elsif (ie_opcao_p = 'F') then
		select	count(*)
		into STRICT	vl_retorno_w
		from	atendimento_paciente
		where	(dt_alta IS NOT NULL AND dt_alta::text <> '');
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
