CREATE OR REPLACE FUNCTION separa_nome_composto (ds_campo_p text, nr_campo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(200);
ds_retorno2_w	varchar(200);
i		integer;
primeiro	boolean;


BEGIN
primeiro := true;

if (ds_campo_p IS NOT NULL AND ds_campo_p::text <> '') then
	begin

	for	i in 1 .. length(ds_campo_p) loop
		begin
		if (primeiro = false) then
			ds_retorno2_w	:= ds_retorno2_w || substr(ds_campo_p,i,1);
		end if;

		if	(not (substr(ds_campo_p,i,1) = (' '))) and (primeiro = true) then
			ds_retorno_w	:= ds_retorno_w || substr(ds_campo_p,i,1);
		else
			primeiro := false;
		end if;
		end;
	end loop;
	end;
end if;

if (nr_campo_p = 1) then
	return ds_retorno_w;
elsif (nr_campo_p = 2) then
	return ds_retorno2_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
