CREATE OR REPLACE FUNCTION shift_obter_conversao_externa (nr_opcao_p bigint, ds_parametro_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_opcao_p = 1) then

	select	obter_conversao_externa(cd_cgc,'SETOR_ATENDIMENTO','CD_SETOR_ATENDIMENTO',ds_parametro_p)
	into STRICT	ds_retorno_w
	from	conversao_meio_externo
	where	ie_sistema_externo = 'S'
	and		cd_cgc = (SELECT max(cd_cgc)
					  from pessoa_juridica_estab
					  where 101426 = nr_seq_xml_lab_envio) LIMIT 1;

elsif (nr_opcao_p = 2) then

	select	obter_conversao_externa(cd_cgc,'CONVENIO_PLANO','CD_CONVENIO',ds_parametro_p)
	into STRICT	ds_retorno_w
	from	conversao_meio_externo
	where	ie_sistema_externo = 'S'
	and		cd_cgc = (SELECT max(cd_cgc)
					  from pessoa_juridica_estab
					  where 101426 = nr_seq_xml_lab_envio) LIMIT 1;

elsif (nr_opcao_p = 3) then

	select	obter_conversao_externa(cd_cgc,'CONVENIO_PLANO','CD_PLANO',ds_parametro_p)
	into STRICT	ds_retorno_w
	from	conversao_meio_externo
	where	ie_sistema_externo = 'S'
	and		cd_cgc = (SELECT max(cd_cgc)
					  from pessoa_juridica_estab
					  where 101426 = nr_seq_xml_lab_envio) LIMIT 1;

elsif (nr_opcao_p = 4) then

	select	obter_conversao_externa(cd_cgc,'ABRANGENCIA_PLANO','NR_SEQUENCIA',ds_parametro_p)
	into STRICT	ds_retorno_w
	from	conversao_meio_externo
	where	ie_sistema_externo = 'S'
	and		cd_cgc = (SELECT max(cd_cgc)
					from pessoa_juridica_estab
					where 101426 = nr_seq_xml_lab_envio) LIMIT 1;

else
	ds_retorno_w := obter_desc_expressao(509632);--'valor inv√°lido';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
