CREATE OR REPLACE FUNCTION show_children (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

	ds_return_w varchar(32767);	
	nm_live_birth_w bigint;
	nm_still_birth_w bigint;
	temp_w bigint;

BEGIN
select	nm_live_birth_w, nm_still_birth_w into STRICT nm_live_birth_w, nm_still_birth_w
from	epc_psm_data
where	cd_pessoa_fisica = cd_pessoa_fisica_p;

temp_w := nm_live_birth_w - 1;
if (nm_still_birth_w = 0) or (coalesce(nm_still_birth_w::text, '') = '') then
ds_return_w := temp_w;
else
ds_return_w := temp_w || 'st' || nm_still_birth_w;
end if;
return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
