CREATE OR REPLACE FUNCTION show_other_children (CD_PESSOA_FISICA_P text) RETURNS varchar AS $body$
DECLARE

	DS_RETURN_W varchar(32767);	
	NM_LIVE_BIRTH bigint;
	NM_STILL_BIRTH bigint;
	TEMP bigint;

BEGIN
SELECT	NM_LIVE_BIRTH , NM_STILL_BIRTH INTO STRICT NM_LIVE_BIRTH , NM_STILL_BIRTH
FROM	EPC_PSM_DATA
WHERE	CD_PESSOA_FISICA = CD_PESSOA_FISICA_P;

TEMP := NM_LIVE_BIRTH - 1;
IF (NM_STILL_BIRTH = 0) OR (coalesce(NM_STILL_BIRTH::text, '') = '') THEN
DS_RETURN_W := TEMP;
ELSE
DS_RETURN_W := TEMP || 'ST' || NM_STILL_BIRTH;
END IF;
RETURN	DS_RETURN_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
