CREATE OR REPLACE FUNCTION show_pob_desc (CD_PESSOA_FISICA_P text , INDICATOR_P bigint) RETURNS varchar AS $body$
DECLARE

	DS_POB_DES_W varchar(100);	
	NM_CHILD_POB_W bigint;
	NM_FATHER_POB_W bigint;
	NM_MOTHER_POB_W bigint;

BEGIN
SELECT NM_CHILD_POB,NM_FATHER_POB,NM_MOTHER_POB INTO STRICT NM_CHILD_POB_W,NM_FATHER_POB_W,NM_MOTHER_POB_W FROM EPC_PSM_DATA WHERE CD_PESSOA_FISICA = CD_PESSOA_FISICA_P;
CASE INDICATOR_P
	WHEN 1 THEN
	SELECT CASE WHEN(SELECT SUBSTR(OBTER_DESC_CEP_LOC(NM_CHILD_POB_W),1,50) || ' - ' || SUBSTR(OBTER_UF_LOCALIDADE(NM_CHILD_POB_W),1,2) )=' - ' THEN  NULL  ELSE (SELECT SUBSTR(OBTER_DESC_CEP_LOC(NM_CHILD_POB_W),1,50) || ' - ' || SUBSTR(OBTER_UF_LOCALIDADE(NM_CHILD_POB_W),1,2) ) END  INTO STRICT DS_POB_DES_W;
	WHEN 2 THEN
	SELECT CASE WHEN(SELECT SUBSTR(OBTER_DESC_CEP_LOC(NM_FATHER_POB_W),1,50) || ' - ' || SUBSTR(OBTER_UF_LOCALIDADE(NM_FATHER_POB_W),1,2) )=' - ' THEN  NULL  ELSE (SELECT SUBSTR(OBTER_DESC_CEP_LOC(NM_FATHER_POB_W),1,50) || ' - ' || SUBSTR(OBTER_UF_LOCALIDADE(NM_FATHER_POB_W),1,2) ) END  INTO STRICT DS_POB_DES_W;
	WHEN 3 THEN
	SELECT CASE WHEN(SELECT SUBSTR(OBTER_DESC_CEP_LOC(NM_MOTHER_POB_W),1,50) || ' - ' || SUBSTR(OBTER_UF_LOCALIDADE(NM_MOTHER_POB_W),1,2) )=' - ' THEN  NULL  ELSE (SELECT SUBSTR(OBTER_DESC_CEP_LOC(NM_MOTHER_POB_W),1,50) || ' - ' || SUBSTR(OBTER_UF_LOCALIDADE(NM_MOTHER_POB_W),1,2) ) END  INTO STRICT DS_POB_DES_W;
	
END CASE;
RETURN	DS_POB_DES_W;

END;
$body$
