CREATE OR REPLACE FUNCTION show_selected_comments (nr_seq_std_comment_p bigint) RETURNS varchar AS $body$
DECLARE

	ds_return_w varchar(32767);			

BEGIN
select	b.ds_comment into STRICT ds_return_w
from	cpoe_std_comment b,
		cpoe_comment_linkage a
where	a.nr_seq_std_comment = b.nr_sequencia
and		a.nr_seq_std_comment = nr_seq_std_comment_p;

return	ds_return_w;

end;
$body$
