CREATE OR REPLACE FUNCTION sinan_dados_medico ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p
'C' - Retorna o código de pessoa física do médico
'R' - Retorna o código CRM do médico
'N' - Retorna o nome de pessoa física do médico
*/
cd_medico_w		diagnostico_doenca.cd_medico%type;
ds_retorno_w		varchar(255);

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	select	max(c.cd_medico)
	into STRICT	cd_medico_w
	from	diagnostico_doenca c,
		cih_doenca_compulsoria d
	where	c.nr_atendimento = nr_atendimento_p
	and	d.ie_situacao = 'A';

	if (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') then

		select	CASE WHEN ie_opcao_p='C' THEN  cd_medico_w WHEN ie_opcao_p='R' THEN  max(b.nr_crm) WHEN ie_opcao_p='N' THEN  max(a.nm_pessoa_fisica)  ELSE null END
		into STRICT	ds_retorno_w
		from	pessoa_fisica a,
			medico b
		where	a.cd_pessoa_fisica = cd_medico_w
		and	a.cd_pessoa_fisica = b.cd_pessoa_fisica;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
