SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sinan_obter_cargo (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

cd_cargo_w	bigint;
ds_retorno_w	varchar(80);	
ie_ocupacao_w	varchar(1);
		

BEGIN 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
	ie_ocupacao_w := obter_param_usuario(1137, 6, obter_perfil_ativo, OBTER_USUARIO_ATIVO, OBTER_ESTABELECIMENTO_ATIVO, ie_ocupacao_w);
 
	if (ie_ocupacao_w = 'C') then 
	 
		select 	max(cd_cargo) 
		into STRICT	cd_cargo_w 
		from 	pessoa_fisica 
		where 	cd_pessoa_fisica = cd_pessoa_fisica_p;
 
		select 	substr(ds_cargo,1,80) 
		into STRICT	ds_retorno_w 
		from  	cargo 
		where 	cd_cargo = cd_cargo_w;
		 
		 
	end if;
	if (ie_ocupacao_w = 'P') then 
		 
		select	max(substr(obter_desc_profissao(cd_profissao),1,80)) 
		into STRICT	ds_retorno_w 
		from	compl_pessoa_fisica 
		where	cd_pessoa_fisica = cd_pessoa_fisica_p 
		and	ie_tipo_complemento = 1;
		 
	end if;
end if;
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION sinan_obter_cargo (cd_pessoa_fisica_p text) FROM PUBLIC;

