CREATE OR REPLACE FUNCTION sinan_obter_cbo_sus (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

cd_cbo_sus_w	integer;
ds_retorno_w	varchar(80);

BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select 	max(cd_cbo_sus)
	into STRICT	cd_cbo_sus_w
	from 	pessoa_fisica
	where 	cd_pessoa_fisica = cd_pessoa_fisica_p;

	select 	max(ds_cbo)
	into STRICT	ds_retorno_w
	from   	cbo_red
	where  	cd_cbo = cd_cbo_sus_w;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
