SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sinan_obter_dados_antec_epidem (nr_seq_notificacao_p bigint, ie_opcao_p text, nr_linha_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_data_registro_w	varchar(255);
ds_uf_w			varchar(255);
ds_municipio_w		varchar(255);
ds_pais_w		varchar(255);
ds_meio_transporte_w	varchar(255);
linha_w			varchar(2);

/*
D   - Data
UF - UF
ML - Municipio/Localidade
P   - Pais
MT - Meio de Transporte*/
BEGIN
if ((nr_seq_notificacao_p IS NOT NULL AND nr_seq_notificacao_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') and (nr_linha_p IS NOT NULL AND nr_linha_p::text <> '')) then
   begin
   select *
   into STRICT	ds_data_registro_w,
	ds_municipio_w,
	ds_uf_w,
	ds_pais_w,
	ds_meio_transporte_w,
	linha_w
   from (SELECT	b.dt_registro,
		b.ds_municipio,
		b.sg_estado,
		substr(obter_nome_pais(b.nr_seq_pais),1,255),
		b.ds_meio_transporte,
		row_number() OVER () AS linha
        from	sinan_inf_deslocamento b
        where	b.nr_seq_notificacao = nr_seq_notificacao_p) alias6

   where linha = nr_linha_p;

   if (ie_opcao_p = 'D') then
	ds_retorno_w := ds_data_registro_w;
   elsif (ie_opcao_p = 'UF') then
	ds_retorno_w := ds_uf_w;
   elsif (ie_opcao_p = 'ML') then
	ds_retorno_w := ds_municipio_w;
   elsif (ie_opcao_p = 'P') then
	ds_retorno_w := ds_pais_w;
   elsif (ie_opcao_p = 'MT') then
	ds_retorno_w := ds_meio_transporte_w;
   end if;
   end;
   end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
