CREATE OR REPLACE FUNCTION sinan_obter_desc_cargo_pf (nr_seq_notificacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(80);
cd_cbo_sus_w	integer;


BEGIN
if (nr_seq_notificacao_p IS NOT NULL AND nr_seq_notificacao_p::text <> '') then
	select 	max(c.cd_cbo_sus)
	into STRICT	cd_cbo_sus_w
	from	notificacao_sinan b,
		pessoa_fisica c
	where	b.nr_sequencia = nr_seq_notificacao_p
	and	b.cd_paciente = c.cd_pessoa_fisica;


	select 	max(ds_cbo)
	into STRICT	ds_retorno_w
	from   	cbo_red
	where  	cd_cbo = cd_cbo_sus_w;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
