CREATE OR REPLACE FUNCTION sinan_obter_estado_civil (nr_seq_notificacao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_paciente_w	varchar(10);
ds_retorno_w	varchar(254);

BEGIN
if (nr_seq_notificacao_p IS NOT NULL AND nr_seq_notificacao_p::text <> '') then
	select	max(cd_paciente)
	into STRICT	cd_paciente_w
	from   	notificacao_sinan
	where  	nr_sequencia = nr_seq_notificacao_p;

	select 	max(substr(obter_valor_dominio(5,IE_ESTADO_CIVIL),1,254))
	into STRICT	ds_retorno_w
	from 	pessoa_fisica
	where	cd_pessoa_fisica = cd_paciente_w;
end if;

return	ds_retorno_w;

end;
$body$
