CREATE OR REPLACE FUNCTION sinan_obter_nome_mae ( nr_seq_notificacao_p bigint, cd_paciente_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);


BEGIN 
 
if (nr_seq_notificacao_p IS NOT NULL AND nr_seq_notificacao_p::text <> '') then 
	 
	select substr(a.nm_mae,1,255) 
	into STRICT	ds_retorno_w 
	from 	notificacao_sinan a 
	where	a.nr_sequencia = nr_seq_notificacao_p;
	 
	if (coalesce(ds_retorno_w::text, '') = '') then 
		select substr(obter_nome_pf(a.cd_pessoa_mae),1,255) 
		into STRICT	ds_retorno_w 
		from 	notificacao_sinan a 
		where	a.nr_sequencia = nr_seq_notificacao_p;
	end if;
	 
	if (cd_paciente_p IS NOT NULL AND cd_paciente_p::text <> '') then 
 
		if (coalesce(ds_retorno_w::text, '') = '') then		 
			select substr(obter_compl_pf(cd_paciente_p,5,'N'),1,255) 
			into STRICT	ds_retorno_w 
			;
		end if;
		 
		if (coalesce(ds_retorno_w::text, '') = '') then		 
			select substr(obter_nome_pf(a.cd_pessoa_mae),1,255) 
			into STRICT	ds_retorno_w 
			from	pessoa_fisica a 
			where  a.cd_pessoa_Fisica = cd_paciente_p;
		end if;
		 
	end if;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
