CREATE OR REPLACE FUNCTION sinan_obter_period_agravo ( nr_seq_doenca_compulsoria_p bigint) RETURNS varchar AS $body$
DECLARE

ds_periodicidade_w		varchar(255);


BEGIN

if (nr_seq_doenca_compulsoria_p IS NOT NULL AND nr_seq_doenca_compulsoria_p::text <> '') then
	select max(substr(obter_valor_dominio(8350,ie_periodicidade),1,255))
	into STRICT ds_periodicidade_w
	from cih_doenca_compulsoria
	where nr_sequencia = nr_seq_doenca_compulsoria_p
	and   ie_situacao = 'A';
end if;

return	ds_periodicidade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
