CREATE OR REPLACE FUNCTION sip_consistir_carencia_exposto ( nr_seq_tipo_carencia_p bigint, nr_seq_segurado_p bigint, nr_seq_contrato_p bigint, nr_seq_plano_p bigint, dt_inclusao_operadora_p timestamp) RETURNS timestamp AS $body$
DECLARE


ie_mes_posterior_w		varchar(1);
qt_registro_w			bigint;
qt_dias_w			bigint	:= 0;
dt_exposto_w			timestamp;
dt_inicio_vigencia_w		timestamp;


BEGIN
dt_exposto_w	:= dt_inclusao_operadora_p;

select	count(nr_sequencia)
into STRICT	qt_registro_w
from	pls_carencia
where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p
and	nr_seq_segurado		= nr_seq_segurado_p  LIMIT 1;

if (qt_registro_w	= 0) then
	select	count(nr_sequencia)
	into STRICT	qt_registro_w
	from	pls_carencia
	where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p
	and	nr_seq_contrato		= nr_seq_contrato_p  LIMIT 1;

	if (qt_registro_w	= 0) then
		select	count(nr_sequencia)
		into STRICT	qt_registro_w
		from	pls_carencia
		where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p
		and	nr_seq_plano 		= nr_seq_plano_p  LIMIT 1;

		if (qt_registro_w > 0) then
			select	max(coalesce(a.ie_mes_posterior,'N')),
				max(a.dt_inicio_vigencia),
				max(a.qt_dias)
			into STRICT	ie_mes_posterior_w,
				dt_inicio_vigencia_w,
				qt_dias_w
			from	pls_carencia	a
			where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p
			and	nr_seq_plano 		= nr_seq_plano_p;
		end if;
	else
		select	max(coalesce(a.ie_mes_posterior,'N')),
			max(a.dt_inicio_vigencia),
			max(a.qt_dias)
		into STRICT	ie_mes_posterior_w,
			dt_inicio_vigencia_w,
			qt_dias_w
		from	pls_carencia	a
		where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p
		and	nr_seq_contrato		= nr_seq_contrato_p;
	end if;
else
	select	max(coalesce(a.ie_mes_posterior,'N')),
		max(a.dt_inicio_vigencia),
		max(a.qt_dias)
	into STRICT	ie_mes_posterior_w,
		dt_inicio_vigencia_w,
		qt_dias_w
	from	pls_carencia	a
	where	a.nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p
	and	a.nr_seq_segurado	= nr_seq_segurado_p;
end if;

if (qt_registro_w > 0) then
	dt_exposto_w	:= (coalesce(dt_inicio_vigencia_w,dt_inclusao_operadora_p) + qt_dias_w);
end if;

return	dt_exposto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
